sysauto_script.script:
gs.debug("DiscoverTasker - Offices PURGE script");

var totalOffices = 0;
// RAJ - STRY0011695 - change to use Glide Aggregate
var gaOffices = new GlideAggregate('x_153465_discovert_discover_office_hierarchy');
gaOffices.addAggregate('COUNT');
gaOffices.query();
if(gaOffices.next()){
	totalOffices = gaOffices.getAggregate('COUNT');
}

var offices = new GlideRecord('x_153465_discovert_discover_office_hierarchy');

offices.query();

gs.debug("DiscoverTasker - Offices PURGE script - Offices Row Count:" + totalOffices);

// wipe everything found
offices.deleteMultiple();
gs.debug("DiscoverTasker - Offices PURGE script - Offices deleted:" + totalOffices);
