sysauto_script.script:
gs.debug("DiscoverTasker - Category/Sub Category PURGE script");

var totalCategories = 0;
// RAJ - STRY0011695 - change to use Glide Aggregate
var gaCategories = new GlideAggregate('x_153465_discovert_discover_tasker_category');
gaCategories.addAggregate('COUNT');
gaCategories.query();
if(gaCategories.next()){
	totalCategories= gaCategories.getAggregate('COUNT');
}

var grCategories = new GlideRecord('x_153465_discovert_discover_tasker_category');

grCategories.query();
// NOTE: sub categories have a cascading delete set on the Category field.  So, they should be cleared by this script also


gs.debug("DiscoverTasker - Category/Sub Category PURGE script - Category Row Count:" + totalCategories);

// wipe everything found
grCategories.deleteMultiple();
gs.debug("DiscoverTasker - Category/Sub Category PURGE script - Categories deleted:" + totalCategories);
