sysauto_script.script:
gs.debug("Tasker PURGE script");

var gDate = new GlideDate();
gDate.setValue(gs.daysAgo(gs.getProperty('x_153465_discovert.PurgeDays')));

// RAJ - STRY0011695 - change to use Glide Aggregate
var totalTasks = 0;
var gaTaskInstances = new GlideAggregate('x_153465_discovert_route_task_instance');
//MUST MATCH QUERY BELOW
gaTaskInstances.addQuery('sys_created_on','<',gDate);
gaTaskInstances.addQuery('demo_data', true);
gaTaskInstances.addAggregate('COUNT');
gaTaskInstances.query();
if(gaTaskInstances.next()){
	totalTasks = gaTaskInstances.getAggregate('COUNT');
}

// Query the database and find tasks
// RAJ - may want to do a last 90 days for this
// also may only want to purge demo data in the future
var taskInstances = new GlideRecord('x_153465_discovert_route_task_instance');
gs.debug("Tasker PURGE script - Purge Date:" + gDate.toString());
//Select tasks in unAssigned state.
taskInstances.addQuery('sys_created_on','<',gDate);
taskInstances.addQuery('demo_data', true);
taskInstances.query();

gs.debug("Tasker PURGE script - Task Instance Row Count:" + totalTasks);

// wipe everything found
taskInstances.deleteMultiple();
gs.debug("Tasker PURGE script - Task Instances deleted:" + totalTasks);


// RAJ - STRY0011695 - change to use Glide Aggregate
var totalProcesses = 0;
var gaProcessInstances = new GlideAggregate('x_153465_discovert_discover_routing_runtime');
// NEEDS TO MATCH QUERY BELOW
gaProcessInstances.addQuery('sys_created_on','<',gDate);
gaProcessInstances.addQuery('demo_data', true);
gaProcessInstances.addAggregate('COUNT');
gaProcessInstances.query();
if(gaProcessInstances.next()){
	totalProcesses = gaProcessInstances.getAggregate('COUNT');
}

// Wipe out the Process Instances also
var processInstances = new GlideRecord('x_153465_discovert_discover_routing_runtime');
//Select tasks in unAssigned state.
processInstances.addQuery('sys_created_on','<',gDate);
taskInstances.addQuery('demo_data', true);
processInstances.query();

gs.debug("Tasker PURGE script - Process Instance Row Count:" + totalProcesses);

// wipe everything found
processInstances.deleteMultiple();
gs.debug("Tasker PURGE script - Process Instances deleted:" + totalProcesses);
