sysauto_script.script:
gs.debug("Tasker Demo Data - 2 Assign some Unassigned Tasks");

// RAJ - STRY0011695 - change to use Glide Aggregate
var totalTasks = 0;
var gaTaskInstances = new GlideAggregate('x_153465_discovert_route_task_instance');
gaTaskInstances.addQuery('demo_data', true);
gaTaskInstances.addQuery('state', -5);
gaTaskInstances.addNotNullQuery('primary_assignee');
gaTaskInstances.addAggregate('COUNT');
gaTaskInstances.query();
if(gaTaskInstances.next()){
	totalTasks = gaTaskInstances.getAggregate('COUNT');
}

// Query the database and find tasks
var taskInstances = new GlideRecord('x_153465_discovert_route_task_instance');
var gDate = new GlideDate();
//gDate.getByFormat("dd-MM-yyyy");

//Select tasks in unAssigned state.
taskInstances.addQuery('state', -5);

// let's not worry about start dates for demo data
//taskInstances.addQuery('expected_start', 'STARTSWITH', gDate);
//taskInstances.addQuery('work_start', null);
// RAJ STRY0011656 - this allows us to ONLY move tasks forward that are demo data tasks
taskInstances.addQuery('demo_data', true);
taskInstances.addNotNullQuery('primary_assignee');
taskInstances.query();
var countTasksAssigned = 0;
var count = 0;
var sPrimaryAssignee = '';
gs.info("Tasker Demo Data - 2 Assign some Unassigned Tasks - Starting Number unassigned tasks: " + totalTasks);
// start pending task on expected start date only
while(count < (totalTasks / 2) && taskInstances.next()) {
	sPrimaryAssignee = taskInstances.getValue('primary_assignee');
	if(sPrimaryAssignee != null) {
		// RAJ - STRY0011695 - change to use Glide Aggregate
		var numRecs = 0;
		var gaAssignTo = new GlideAggregate('x_153465_discovert_discover_office_members');
		gaAssignTo.addQuery('discover_office_hierarchy', sPrimaryAssignee);
		gaAssignTo.addAggregate('COUNT');
		gaAssignTo.query();
		if(gaAssignTo.next()){
			numRecs = gaAssignTo.getAggregate('COUNT');
		}

		var assignTo = new GlideRecord('x_153465_discovert_discover_office_members');
		assignTo.addQuery('discover_office_hierarchy', sPrimaryAssignee);
		assignTo.query();

		//Set impersonation.
		// RAJ STRY0011656 - this was not working
		//gs.getSession().impersonate(taskInstances.assigned_to);

		//var randomNumber = Math.floor(Math.random()*numRecs);
		//TODO - Figure out more random value.
		var randomNumber = numRecs/gDate.getDayOfMonthNoTZ();
		var rndSecs = Math.floor(Math.random() * 10); //seconds in a day
		gs.debug("Tasker Demo Data - 2 Assign some Unassigned Tasks - Random: " + rndSecs);
		var randomCount = 0;
		while (assignTo.next() && randomCount < randomNumber){
			gs.debug("Tasker Demo Data - 2 Assign some Unassigned Tasks - Assignee: " + assignTo.office_member);
			randomCount++;
		}
		taskInstances.work_start = gDate.getByFormat("dd-MM-yyyy");
		//Set state to Assigned.
		taskInstances.state = 1;
		taskInstances.assigned_to = assignTo.office_member;
		taskInstances.update();
		gs.debug("Tasker Demo Data - 2 Assign some Unassigned Tasks - Task Number: " + taskInstances.number);
		countTasksAssigned++;
	}
	count++;
}

gs.info("Tasker Demo Data - 2 Assign some Unassigned Tasks - tasks assigned by the system: " + countTasksAssigned);
