sysauto_script.script:
gs.debug("Tasker Demo Data - 6 Move some tasks to Review");

// RAJ - STRY0011695 - change to use Glide Aggregate
var totalTasks = 0;
var gaTaskInstances = new GlideAggregate('x_153465_discovert_route_task_instance');
gaTaskInstances.addQuery('state', 12).addOrCondition('state', 13);
gaTaskInstances.addQuery('demo_data', true);
gaTaskInstances.addAggregate('COUNT');
gaTaskInstances.query();
if(gaTaskInstances.next()){
	totalTasks = gaTaskInstances.getAggregate('COUNT');
}

// Query the database and find tasks
var taskInstances = new GlideRecord('x_153465_discovert_route_task_instance');
var gDate = new GlideDate();

//Select tasks in Work Scheduled or Work in Progress states.
taskInstances.addQuery('state', 12).addOrCondition('state', 13);
taskInstances.addQuery('demo_data', true);
taskInstances.query();
var count = 0;

gs.debug("Tasker Demo Data - 6 Move some tasks to Review - Starting Moving Tasks from Scheduled or Working into Review state: " + totalTasks);


// review tasks - reduce the number sent to review to 1 in 4
while(count < (totalTasks / 4) && taskInstances.next()){
	// move to review state
	taskInstances.state = 14;
	taskInstances.update();
	count++;
}

gs.info("Tasker Demo Data - 6 Move some tasks to Review - tasks sent to review by the system: " + count);
