sysauto_script.script:
gs.debug("Tasker Demo Data - 12 create some risk");

// RAJ - STRY0011695 - change to use Glide Aggregate
var totalTasks = 0;
var gaTaskInstances = new GlideAggregate('x_153465_discovert_route_task_instance');
gaTaskInstances.addQuery('active', true);
gaTaskInstances.addQuery('demo_data', true);
gaTaskInstances.addAggregate('COUNT');
gaTaskInstances.query();
if(gaTaskInstances.next()){
	totalTasks = gaTaskInstances.getAggregate('COUNT');
}

gs.info("Tasker Demo Data - 12 Create some risk - Starting toal Task: " + totalTasks);

var totalTaskWithRisk = 0;
var gaTaskwithRisk = new GlideAggregate('x_153465_discovert_route_task_instance');
gaTaskwithRisk.addQuery('risk_flag','>',0);
gaTaskwithRisk.addQuery('active', true);
gaTaskwithRisk.addQuery('demo_data', true);
gaTaskwithRisk.addAggregate('COUNT');
gaTaskwithRisk.query();
if(gaTaskwithRisk.next()){
	totalTaskWithRisk = gaTaskwithRisk.getAggregate('COUNT');
}

gs.info("Tasker Demo Data - 12 Create some risk - Starting toal Task: " + totalTasks);
gs.info("Tasker Demo Data - 12 Create some risk - Starting Risk Count: " + totalTaskWithRisk);
//gs.info("Tasker Demo Data - 12 Create some risk - Starting Risk %: " + (totalTaskWithRisk/totalTasks));

// Query the database and find tasks
var taskInstances = new GlideRecord('x_153465_discovert_route_task_instance');
var gDate = new GlideDate();

//Select tasks in Work Scheduled or Work in Progress, or In Review states.
taskInstances.addQuery('risk_flag','<',1);
taskInstances.addQuery('active', true);
taskInstances.addQuery('demo_data', true);
taskInstances.query();
var count = 0;

// calculate curent risk level

// complete tasks - reduce the number completed b/c it is across multiple states to 1 in 5
while((count + totalTaskWithRisk  < (totalTasks / 8)) && taskInstances.next()){
	// move to incomplete state
	taskInstances.work_notes = "risk added by System";
	if(count + totalTaskWithRisk  < (totalTasks / 16)){
		taskInstances.risk_flag = 1;
		taskInstances.risk_impact_description = "unlikely to finish - " + taskInstances.short_description + " - on time";
		taskInstances.risk_impact_factor = 'minor';
		taskInstances.risk_mitigation_strategy = "Report to superior and track";
		taskInstances.risk_probability = 'unlikely';
		taskInstances.risk_probability_description = 'this is where you would define all of the things that would have to happen for this unlikely event to occur';
		taskInstances.risk_reason = "Short staffing due to vacation caused some tasks to be delayed";
	}
	else{
		taskInstances.risk_flag = 2;
		taskInstances.risk_impact_description = "VERY unlikely to finish - " + taskInstances.short_description + " - on time";
		taskInstances.risk_impact_factor = 'major';
		taskInstances.risk_mitigation_strategy = "Working to create a full mitigation report to provide to superiors and track";
		taskInstances.risk_probability = 'likely';
		taskInstances.risk_probability_description = 'this is where you would define all of the things that already happened and are making this more likely to occur';
		taskInstances.risk_reason = "MAJOR system outage caused lots of tasks to be delayed";
	}
	taskInstances.update();
	count++;
}

gs.info("Tasker Demo Data - 12 Create some risk - tasks updated by the system: " + count);
