sysevent_script_action.script:
// RAJ - this event starts the process to generate a process from a Tasker Tree
//gs.info('DiscoverTasker - Start Save as Template Event Script Action - Event Parm2: ' + event.parm2);

gs.info('DiscoverTasker - Start Save as Template Event Script Action - Session created');
	var recCreateTemp = new GlideRecord('x_153465_discovert_discover_routing_template');
 	recCreateTemp.initialize();
	// Set the Generate From TaskerInstance associated with the process template
	// RAJ -- TODO ??? do I just start a process here and pass in the Task Instance ID instead of doing it exactly like running a process?
	// or, do I just build it out exactly in reverse order of running a proc?

 	recCreateTemp.setValue ( 'original_tasker_saved_to_process', current.sys_id );
gs.info('Discover Tasker - Save Template Event Script Action - originating tasker: ' + recCreateTemp.original_tasker_saved_to_process);
 	// that is it, workflow is still going to fire off the insert of the Process Instance table
	recCreateTemp.process_owner = event.parm2;
	recCreateTemp.template_name = current.short_description; 
	recCreateTemp.short_description = current.short_description; 
	recCreateTemp.description = current.instructions; 

	recCreateTemp.process_level = 'my_process';
 	recCreateTemp.insert();

//  TODO, we might want to just start the process to generate a process here instead of starting it off of the insert.
// it would keep us from having to create a field relating the originating tasker just for this
